(function () {
  function openNewsletterModal() {
    var backdrop = document.getElementById('newsletter-backdrop');
    if (!backdrop) return;
    backdrop.classList.remove('newsletter-hidden');
  }

  window.closeNewsletterModal = function () {
    var backdrop = document.getElementById('newsletter-backdrop');
    if (!backdrop) return;
    backdrop.classList.add('newsletter-hidden');
  };

  window.addEventListener('load', function () {
    try {
      if (sessionStorage.getItem('newsletter_seen')) return;
    } catch (e) {}

    sessionStorage.setItem('newsletter_seen', '1');
    setTimeout(openNewsletterModal, 500);
  });
})();
